# --- 登錄檔設定 ---
REG_PATH = r"SOFTWARE\Microsoft\WindowsUpdate\UX\Settings"
REG_KEYS = {
    "FeatureStartTime": "PauseFeatureUpdatesStartTime",
    "FeatureEndTime": "PauseFeatureUpdatesEndTime",
    "QualityStartTime": "PauseQualityUpdatesStartTime",
    "QualityEndTime": "PauseQualityUpdatesEndTime",
    "ExpiryTime": "PauseUpdatesExpiryTime",
}

# --- 登錄檔操作核心函數 ---
def datetime_to_reg_sz(dt: datetime) -> str:
    """將 Python datetime 物件轉換為 REG_SZ 所需的 "YYYY-MM-DDTHH:MM:SSZ" 格式 (UTC)。"""
    # 確保轉換為 UTC 時間
    dt_utc = dt.astimezone(timezone.utc)
    return dt_utc.strftime("%Y-%m-%dT%H:%M:%SZ")

def apply_update_pause(new_end_date: date) -> tuple[bool, str]:
    """寫入新的更新暫停結束日期到登錄檔，使用 REG_SZ (字串) 類型。"""
    try:
        new_end_datetime = datetime(new_end_date.year, new_end_date.month, new_end_date.day, 23, 59, 59)
        current_datetime = datetime.now()
        
        expiry_time_str = datetime_to_reg_sz(new_end_datetime)
        current_time_str = datetime_to_reg_sz(current_datetime)

        hkey = winreg.OpenKey(winreg.HKEY_LOCAL_MACHINE, REG_PATH, 0, winreg.KEY_SET_VALUE | winreg.KEY_WRITE)

        # 寫入 REG_SZ 鍵值
        winreg.SetValueEx(hkey, REG_KEYS["FeatureEndTime"], 0, winreg.REG_SZ, expiry_time_str)
        winreg.SetValueEx(hkey, REG_KEYS["QualityEndTime"], 0, winreg.REG_SZ, expiry_time_str)
        winreg.SetValueEx(hkey, REG_KEYS["ExpiryTime"], 0, winreg.REG_SZ, expiry_time_str)
        winreg.SetValueEx(hkey, REG_KEYS["FeatureStartTime"], 0, winreg.REG_SZ, current_time_str)
        winreg.SetValueEx(hkey, REG_KEYS["QualityStartTime"], 0, winreg.REG_SZ, current_time_str)
        
        winreg.CloseKey(hkey)
        return True, f"✅ 更新將暫停至 {new_end_date.strftime('%Y 年 %m 月 %d 日')}"

    except PermissionError:
        # 捕捉權限錯誤，並回傳需要管理員的提示
        return False, "❌ 寫入登錄檔失敗：**此操作需要管理員權限！** 請以管理員身份運行。"
    except Exception as e:
        return False, f"❌ 寫入登錄檔時發生錯誤: {e}"

def cancel_update_pause() -> tuple[bool, str]:
    """刪除延期相關鍵值，以取消更新暫停。"""
    try:
        hkey = winreg.OpenKey(winreg.HKEY_LOCAL_MACHINE, REG_PATH, 0, winreg.KEY_SET_VALUE | winreg.KEY_WRITE)

        keys_to_delete = [
            REG_KEYS["FeatureStartTime"], REG_KEYS["FeatureEndTime"], 
            REG_KEYS["QualityStartTime"], REG_KEYS["QualityEndTime"],
            REG_KEYS["ExpiryTime"] 
        ]
        
        for key in keys_to_delete:
            try:
                winreg.DeleteValue(hkey, key)
            except FileNotFoundError:
                pass 

        winreg.CloseKey(hkey)
        return True, "✅ Windows 更新暫停已取消。"

    except PermissionError:
        # 捕捉權限錯誤，並回傳需要管理員的提示
        return False, "❌ 操作登錄檔失敗：**此操作需要管理員權限！** 請以管理員身份運行。"
    except Exception as e:
        return False, f"❌ 刪除登錄檔時發生錯誤: {e}"

